import { JSXElementConstructor, ReactElement, ReactNode, ReactPortal, useState } from "react";
import "./DeleteTask.css";
import { Task } from "../../Models/Task";
import axios from "axios";
import { SingleTask } from "../SingleTask/SingleTask";
import notify from "../../Utils/notify";
import { useNavigate } from "react-router-dom";

export function DeleteTask(): JSX.Element {
    const [id,setID] = useState(0);
    const [task,setTask] = useState<Task>();
    const navigate = useNavigate();
    const [show,setShow] = useState(false);

    const areYouSure = ()=>{
        <div className="Box">
                <input type="button" value="Yes, Please delete Task" onClick={()=>{
                            //Todo - Confirm user request (Are you sure?)
                            // remove song
                            axios.delete(`http://localhost:8080/Tasks/DeleteTask/${id}`)
                            .then((result)=>{            
                            // Notify success
                            notify.success("Task was deleted successfully");
                            // navigate to song list
                            navigate("/");
                            })                        
                            .catch(err=>{
                                notify.error("There was a problem deleting the task")
                                console.log(err);
                                navigate("/page404")
                            });;

                        }}/>
            </div>
    }

    const getData = ()=>{
        //axios
        axios.get(`http://localhost:8080/Tasks/GetSingleTask/${id}`).then(res=>{
            console.log(res.data);
            setTask(res.data);
            setShow(true);
        })
    }
    return (
        <div className="DeleteTask">
			<div className="Box">
                <h1>Delete Task</h1><hr /><br/>
                <input type="number" placeholder="ID to delete" onChange={(args)=>setID(args.target.valueAsNumber)}/>
                <input type="button" value="Find Task" onClick={getData}/>
            </div>
            <hr/>
            <table>
                <tr>
                    <th>ID</th>
                    <th>Name</th>
                    <th>Responsible</th>
                    <th>Scheduled For</th>
                    <th>Completed?</th>
                </tr>
                <tr>
                    <td>{task?.id}</td>
                    <td>{task?.name}</td>
                    <td>{task?.responsible.name}</td>
                    <td>{task?.scheduledDate.toString()}</td>
                    <td>{task?.isCompleted}</td>
                </tr>
            </table>
            <br/>
            {/* {show?areYouSure:} */}

        </div>
    );
}
