import { useNavigate, useParams } from "react-router-dom";
import "./ViewTask.css";
import { useEffect, useState } from "react";
import { Task } from "../../Models/Task";
import axios from "axios";
import { Button, ButtonGroup } from "@mui/material";

export function ViewTask(): JSX.Element {
    const params = useParams();
    const navigate = useNavigate();
    const [task,setTask] = useState<Task>();

    useEffect(()=>{
        axios.get(`http://localhost:8080/Tasks/GetSingleTask/${params.taskID}`).then(res=>{
            //console.log(res.data);
            setTask(res.data);
        })
    },[])

    return (
        <div className="ViewTask">
            <table>
                <tr>
                    <th>ID</th>
                    <th>Name</th>
                    <th>Responsible</th>
                    <th>Scheduled For</th>
                    <th>Completed?</th>
                </tr>
            </table>
            <table>
                <tr>
                    <td>{task?.id}</td>
                    <td>{task?.name}</td>
                    <td>{task?.responsible.name}</td>
                    <td>{task?.scheduledDate.toString()}</td>
                    <td>{task?.isCompleted}</td>
                </tr>
            </table>
            <br/>
            <ButtonGroup>
                <Button type="error" color="primary" onClick={() => { navigate("/") }}>Delete</Button>
                <Button color="error" onClick={() => { navigate("/") }}>Update</Button>
            </ButtonGroup>
        </div>
    );
}
